import React, { useState, useEffect } from 'react';
import axios from '../../api';

const Reports = () => {
    const [vulnerabilities, setVulnerabilities] = useState([]);

    // Fetch the vulnerabilities from the backend when the component mounts
    useEffect(() => {
        axios.get('/api/vulnerabilities')
            .then(response => {
                setVulnerabilities(response.data);
            })
            .catch(error => {
                console.error('Error fetching vulnerabilities:', error);
            });
    }, []);

    // Effect to refresh the page on app start-up
    useEffect(() => {
        // Check if the page was just loaded
        if (!localStorage.getItem('pageJustLoaded')) {
            localStorage.setItem('pageJustLoaded', 'true');
            window.location.reload();
        } else {
            // Clear the flag for subsequent loads
            localStorage.removeItem('pageJustLoaded');
        }
    }, []);
    // Helper function to extract contract name from description
    const extractContractName = description => {
        const match = description.match(/\[(\w+)\./);
        return match ? match[1] : "Not specified"; // Return "Not specified" if contract name isn't found
    };

    return (
        <div style={{ padding: '20px', textAlign: 'center' }}>
            <h2>Analysis Results</h2>
            { vulnerabilities.length > 0 ? (
                vulnerabilities.map(vuln => (
                    <div key={vuln.vulnerability_id}>
                        <h3>Vulnerability Type: {vuln.vulnerability_name}</h3>
                        <p>Contract: {extractContractName(vuln.description)}</p>
                        <p>Impact: {vuln.impact}</p>
                        <p>Description: {vuln.description}</p>
                        <p>Location: {vuln.location}</p>
                    </div>
                ))
            ) : (
                <p>No results available at the moment. Please upload a Solidity file on the Audit page for analysis.</p>
            )}
        </div>
    );
}

export default Reports;
