import React, { useState, useEffect } from 'react';

const Reports = () => {
    const [vulnerabilities, setVulnerabilities] = useState([]);

    // Fetch the vulnerabilities from the backend when the component mounts
    useEffect(() => {
        fetch('/api/vulnerabilities') // Adjust the endpoint if necessary
            .then(response => response.json())
            .then(data => setVulnerabilities(data))
            .catch(error => console.error('Error fetching vulnerabilities:', error));
    }, []); // Empty dependency array means this useEffect runs once when the component mounts

    return (
        <div style={{ padding: '20px', textAlign: 'center' }}>
            <h2>Analysis Results</h2>
            {vulnerabilities.length === 0 ? (
                <p>No results available at the moment. Please upload a Solidity file on the Audit page for analysis.</p>
            ) : (
                <div>
                    {vulnerabilities.map((vuln, index) => (
                        <div key={index} style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '10px' }}>
                            <h3>Vulnerability Type: {vuln.vulnerability_name}</h3>
                            <p>Contract: {vuln.contract_name}</p>
                            <p>Impact: {vuln.impact}</p>
                            <p>Description: {vuln.description}</p>
                            {/* Additional data can be displayed as needed */}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default Reports;
