/* Created by : Ali Mohammad Jobaer
Student ID : 103835483 */


import React, { useState } from 'react';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import Paper from '@mui/material/Paper';
import AttachFileIcon from '@mui/icons-material/AttachFile';
import { styled } from '@mui/material/styles';
import Snackbar from '@mui/material/Snackbar';
import Alert from '@mui/material/Alert';
import api from '../../api';

            // Full Screen Paper component styling
            const FullScreenPaper = styled(Paper)({
            minHeight: '100vh',
            display: 'flex',
            flexDirection: 'column',
            padding: '3rem'
            });
            // Hidden styled input for file selection
            const StyledInput = styled('input')({
            display: 'none'
            });

            const Audit = () => {
                // States to manage uploaded file, snackbar visibility and error alert
                const [file, setFile] = useState(null);
                const [openSnackbar, setOpenSnackbar] = useState(false);
                const [showErrorAlert, setShowErrorAlert] = useState(false); 
                
                // Handle file selection
                const handleFileChange = (event) => {
                    const selectedFile = event.target.files[0];
                    if (selectedFile && selectedFile.name.endsWith('.sol')) {
                        setFile(selectedFile);
                    } else {
                        setShowErrorAlert(true);
                        setTimeout(() => {
                            setShowErrorAlert(false);
                        }, 3000); // Auto-hide the error alert after 3 seconds
                    }
                };

                    // Close handlers for alert and snackbar
                    const handleClose = () => {
                        setShowErrorAlert(false);
                    };

                    // Handle the file upload action
                    const handleSubmit = () => {
                        if (file) {
                            const formData = new FormData();
                            formData.append('file', file);
                    
                            // Sending the file to the backend
                            api.post('/api/contracts/upload', formData)
                                .then(response => {
                                    setOpenSnackbar(true);
                                })
                                .catch(error => {
                                    // Capture and display the backend error message
                                    const backendErrorMsg = error.response?.data || 'An error occurred during the upload.';
                                    alert(backendErrorMsg); // Or display this in your UI as needed
                    
                                    setShowErrorAlert(true);
                                    setTimeout(() => {
                                        setShowErrorAlert(false);
                                    }, 3000); // Auto-hide the error alert after 3 seconds
                                });
                        } else {
                            setShowErrorAlert(true);
                            setTimeout(() => {
                                setShowErrorAlert(false);
                            }, 3000); // Auto-hide the error alert after 3 seconds
                        }
                    };

                    return (
                        <FullScreenPaper sx={{ backgroundColor: '#898952', color: '#ffffff' }}>
                            <Container maxWidth="lg">
                                <Box paddingBottom={3}>
                                    <Typography variant="h4" gutterBottom>
                                        Smart Contract Audit
                                    </Typography>
                                    <Typography variant="body1" paragraph>
                                        Upload your Solidity (.sol) file for comprehensive analysis. Our automated system will swiftly pinpoint vulnerabilities to bolster the security of your smart contract.
                                    </Typography>
                                </Box>

                                 {/* File Upload Section */}
                                <Box paddingBottom={3}>
                                    <Typography variant="h6" gutterBottom>
                                        File Upload
                                    </Typography>
                                    <Box display="flex" gap={2} alignItems="center">
                                        <StyledInput
                                            accept=".sol"
                                            type="file"
                                            onChange={handleFileChange}
                                            id="contained-button-file"
                                        />

                                        {/* File picker button */}
                                        <label htmlFor="contained-button-file">
                                            <Button variant="contained" color="primary" startIcon={<AttachFileIcon />} component="span">
                                                Choose File
                                            </Button>
                                        </label>

                                        {/* Display selected file name and upload button */}
                                        <Typography variant="body1" style={{ paddingRight: '10px' }}>
                                            {file ? file.name : ""}
                                        </Typography>
                                        <Button variant="contained" color="primary" startIcon={<CloudUploadIcon />} onClick={handleSubmit}>
                                            Upload
                                        </Button>
                                    </Box>

                                    {/* Alert message for invalid file types */}
                                    {showErrorAlert && (
                                        <Alert onClose={handleClose} severity="error" sx={{ width: '100%', marginTop: '1rem' }}>
                                            Please select a valid Solidity (.sol) file.
                                        </Alert>
                                    )}
                                </Box>
                            </Container>

                            {/* Snackbar notification for successful file upload */}
                            <Snackbar
                                anchorOrigin={{
                                    vertical: 'bottom',
                                    horizontal: 'left',
                                }}
                                open={openSnackbar}
                                autoHideDuration={6000}
                                onClose={handleClose}
                                message="File uploaded successfully. Analysis will commence shortly."
                            />
                        </FullScreenPaper>
                    );
                }
                    export default Audit;
