import React, { useState, useEffect } from 'react';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import { styled } from '@mui/material/styles';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import ListItemIcon from '@mui/material/ListItemIcon';
import ArrowRightAltIcon from '@mui/icons-material/ArrowRightAlt';
import Dialog from '@mui/material/Dialog';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';



                // Custom styled component for full-screen paper style
                const FullScreenPaper = styled(Paper)({
                minHeight: '100vh',
                padding: '3rem',
                backgroundColor: '#984447',
                color: '#ffffff'
                });

                const PreviousReports = () => {
                    const [reports, setReports] = useState([]);// State for storing the list of reports
                    const [selectedReport, setSelectedReport] = useState(null); // State for the currently viewed report in the dialog
                    // Fetch mock reports data once component mounts
                    useEffect(() => {
                        const mockReports = [
                            "Report from 5th August 2023",
                            "Report from 3rd August 2023",
                            "Report from 29th July 2023",
                        ];
                        setReports(mockReports);
                    }, []);
                    // Handle click on a report item to open its content in a dialog
                    const handleReportClick = (report) => {
                        setSelectedReport(report);
                    }
                    // Handle closing the report content dialog
                    const handleClose = () => {
                        setSelectedReport(null);
                    }

                        return (
                            <FullScreenPaper>
                                <Container maxWidth="lg">
                                    <Box paddingBottom={3}>
                                        <Typography variant="h4" gutterBottom>
                                            Retrieve Past Audit Reports
                                        </Typography>
                                        <Typography variant="body1" paragraph>
                                            Below are your past smart contract audit reports:
                                        </Typography>
                                    </Box>
                                    {/* Render list of reports or a message if no reports are available */}
                                    {reports.length ? (
                                        <List>
                                            {reports.map((report, index) => (
                                                <ListItem button key={index} onClick={() => handleReportClick(report)}>
                                                    <ListItemText primary={report} />
                                                    <ListItemIcon>
                                                        <ArrowRightAltIcon />
                                                    </ListItemIcon>
                                                </ListItem>
                                            ))}
                                        </List>
                                    ) : (
                                        <Typography variant="body1" color="textSecondary">
                                            No past reports found.
                                        </Typography>
                                    )}
                                    
                                    {/* Dialog to display content of the clicked report */}
                                    <Dialog open={Boolean(selectedReport)} onClose={handleClose} maxWidth="lg" fullWidth>
                                        <DialogTitle>{selectedReport}</DialogTitle>
                                        <DialogContent>
                                            <Typography variant="body1">
                                                Content of {selectedReport}.
                                            </Typography>
                                        </DialogContent>
                                    </Dialog>
                                </Container>
                            </FullScreenPaper>
                        );
                    }

export default PreviousReports;
